mapper.select:
select b.BOARD_SEQ , b.BOARD_TITLE , b.BOARD_CONT , b.BOARD_VIS , b.BOARD_DATE , b.CATEGORY_SEQ , b.MEMBER_SEQ , c.CATEGORY_NAME
from BOARD b, CATEGORY c
where b.CATEGORY_SEQ = c.CATEGORY_SEQ and b.board_flag = 'T' and b.BOARD_SEQ = #{BOARD_SEQ}

mapper.insert:
INSERT INTO BOARD (
BOARD_SEQ
, BOARD_TITLE
, BOARD_CONT
, BOARD_VIS
, BOARD_DATE
, CATEGORY_SEQ
, MEMBER_SEQ
, BOARD_FLAG
)
values(
BO_SEQ.nextval
, #{BOARD_TITLE}
, #{BOARD_CONT}
, 'T'
, #{BOARD_DATE}
, #{CATEGORY_SEQ}
, #{MEMBER_SEQ}
, 'T'
)

mapper.update:
update BOARD
set
BOARD_SEQ=#{BOARD_SEQ}
,BOARD_TITLE=#{BOARD_TITLE}
,BOARD_CONT=#{BOARD_CONT}
,BOARD_VIS='T'
,BOARD_DATE=#{BOARD_DATE}
,CATEGORY_SEQ=#{CATEGORY_SEQ}
,MEMBER_SEQ=#{MEMBER_SEQ}
where BOARD_SEQ=#{BOARD_SEQ}

UPDATE BOARD 
SET BOARD_FLAG='F' 
WHERE BOARD_SEQ = #{BOARD_SEQ}
